import React, { useEffect } from "react";

import { useParams, useHistory } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import {
  Spin,
  Row,
  Col,
  Image,
  Typography,
  Button,
  Space,
  Modal,
  message,
} from "antd";
import {
  EditOutlined,
  DeleteOutlined,
  ExclamationCircleOutlined,
} from "@ant-design/icons";

import { dispatchDetailProduct } from "../../actions/inventory/action-creator";
import { SET_PRODUCT_ERROR } from "../../actions/inventory/types";
import { deleteProduct } from "../../actions/inventory/api";
import ResultInfo from "../general/ResultInfo";

const { Title, Paragraph } = Typography;
const { confirm } = Modal;

export default function ProductDetail() {
  const { productId } = useParams();
  const dispatch = useDispatch();
  const history = useHistory();

  useEffect(() => {
    dispatch(dispatchDetailProduct(productId));
    return () => {
      dispatch({ type: SET_PRODUCT_ERROR, payload: {} });
    };
  }, []);

  const { error, detailProduct, loading } = useSelector(
    (state) => state.inventoryReducer
  );

  if (loading) {
    return <Spin />;
  } else if (Object.keys(error).length !== 0) {
    return (
      <ResultInfo
        status={error.status}
        title={error.title}
        subTitle={error.subTitle}
      />
    );
  } else {
    const confirmDelete = () =>
      confirm({
        title: `Delete ${detailProduct?.name}`,
        icon: <ExclamationCircleOutlined />,
        content: `Are you sure want to delete this product?`,
        okText: "Yes",
        okType: "danger",

        cancelText: "No",
        async onOk() {
          try {
            await deleteProduct({ product_id: detailProduct.id });
            message.success("Successfully deleted the product");
            history.push("/");
          } catch (error) {
            message.error(
              "Something went wrogn while deleting the product. Try again later"
            );
          }
        },
        onCancel() {
          console.log("Cancel");
        },
      });

    return (
      <>
        <Row gutter={[16, 16]}>
          <Col span={6}>
            <div className="container">
              <Image
                src="https://dynaimage.cdn.cnn.com/cnn/q_auto,w_634,c_fill,g_auto,h_357,ar_16:9/http%3A%2F%2Fcdn.cnn.com%2Fcnnnext%2Fdam%2Fassets%2F200114114006-belugaxl-first-flight-air-to-air-074.jpg"
                fallback="data:image/png;base64,"
              />
            </div>
          </Col>
          <Col span={15}>
            <div className="container">
              <Row gutter={[16, 16]}>
                <Col span={18}>
                  <Title level={2}>{detailProduct?.name}</Title>
                  <Title level={5} style={{ marginTop: -10 }}>
                    {detailProduct?.category?.categoryName}
                  </Title>
                  <Paragraph>{detailProduct?.description}</Paragraph>
                </Col>
                <Col span={6}>
                  <Paragraph>
                    <b> Created: </b>
                    {detailProduct?.createdAt}
                  </Paragraph>
                  <Paragraph>
                    <b> Updated: </b>
                    {detailProduct?.updatedAt}
                  </Paragraph>
                  <Paragraph>
                    <b>Units Left</b>: {detailProduct?.units}
                  </Paragraph>
                </Col>
              </Row>
            </div>
          </Col>
          <Col span={3}>
            <Space direction="vertical" style={{ width: "100%" }}>
              <Button
                type="primary"
                onClick={() =>
                  history.push(`/product/edit/${detailProduct.id}`)
                }
                block
                icon={<EditOutlined />}
              >
                Edit Product
              </Button>
              <Button
                danger
                block
                onClick={confirmDelete}
                style={{ width: "100%" }}
                icon={<DeleteOutlined />}
              >
                Delete Product
              </Button>
            </Space>
          </Col>
        </Row>
      </>
    );
  }
}
